import React, {Component} from 'react';
import family from '../imgs/family.jpg';
import individual from '../imgs/individual.jpg';
import student from '../imgs/student.jpg';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import formPDF from '../docs/membershipForm.pdf';
import formDOC from '../docs/membershipForm.docx';
import '../css/membership.css';



export default class extends Component{
  render(){
    const membershipTypes = [
      {
        type : "Family",
        img : family,
        description : "This membership type is available to families consisting of husband/ wife and their children.",
        pdf : formPDF,
        doc : formDOC

      },
      {
        type : "Individual",
        img : individual,
        description : "This membership type is available to any individual who does not qualify for student membership.",
        pdf : formPDF,
        doc : formDOC
      },
      {
        type : "Student",
        img : student,
        description : "This membership type is available to any student enrolled in college or university.",
        pdf : formPDF,
        doc : formDOC
      }
    ]
    return(
      <div className="membership">
        <div className="membershipTitle">
          <h5>Membership Catagories</h5>
        </div>
          {membershipTypes.map(membership =>
            <div style={{display: 'inline-block', padding: '5px'}}>
              <div className="card">
                <img 
                  className="imgBox"
                  src={membership.img} 
                  alt="Avatar" />
                <div className="TextContainer">
                  <label>{membership.type} Membership</label> 
                </div>
                <div className="txtDescription" >
                  <div style={{textAlign: 'left'}}>
                    {membership.description}
                  </div>
                  <p>Download MEMBERSHIP form!</p>
                  <Container>
        <Row>
          <Col xs="4"><a href={membership.pdf} download>PDF</a></Col>
          {/* <Col xs="auto"></Col> */}
          <Col xs="7"><a href={membership.doc} download>Word Document</a></Col>
        </Row>
      </Container>
                  <table>
                    <td>
                      <a href={membership.pdf} download>PDF</a>
                    </td>
                    <td align="right" style={{textAlign : 'right'}}>
                      <a href={membership.doc} download>Word Document</a>
                    </td>
                  </table>
                </div>
              </div>
            </div>)}
            <br />
            <b>Email completed membership forms to <a href="mailto:info@ionbahamas.org?subject=Mail from Our Site">info@ionbahamas.org</a></b>
    </div>
    )
  }
}
