import React, { Component } from 'react';
import Layout from '../components/Layout';
import axios from 'axios';

class member extends Component {
  constructor(props){
    super(props);
    this.state={
      loading : true,
      err : false,
      value: '',
      anonymous : true
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleCheck = this.handleCheck.bind(this);
  }

  componentWillMount(){

  }

  componentDidMount(){

  }
  componentDidCatch(error, errorInfo){
    this.setState({err : true})
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleCheck(event) {
    this.setState({anonymous: !this.state.anonymous});
    console.log(this.state.anonymous)
  }

  handleSubmit(event) {
    alert('An essay was submitted: ' + this.state.value);
    event.preventDefault();
    axios.post("http://localhost:3001/account/hope/", { value : this.state.value })
      .then(res => {
        console.log(res);
        console.log(res.data);
      })
  }
  
  render() {
    return (
      <div style={{marginTop : '50px'}}>
        <h2>Messages of Hope</h2>
        <form onSubmit={this.handleSubmit}>
          <div>
            <input style={{margin : '10px'}} type="text" id="scales" name="scales" onClick={this.handleCheck}/>
            <input style={{margin : '10px'}} type="text" id="scales" name="scales" onClick={this.handleCheck}/>
            <input type="checkbox" id="scales" name="scales" onClick={this.handleCheck}/>
            <label for="scales">Scales</label>
          </div>
          <center>
            <textarea 
              placeholder="Type your message here..." 
              style={{width: '95%', minHeight: '500px', marginBottom : '10px'}} 
              value={this.state.value} 
              onChange={this.handleChange} 
            />
            <br />
            <input style={{width : '95%'}} disabled={this.state.value.length === 0} type="submit" value="Submit" />
          </center>
      </form>
      </div>
    );
  }
}

export default Layout(member);
