import React, {Component} from 'react';
import InfiniteCarousel from 'react-leaf-carousel';
import '../css/infinitecarousel.css';

export default class extends Component{
  render(){
    const sponsorValues = [
      {img : 'https://vignette.wikia.nocookie.net/konosuba/images/1/14/Aqua-anime.png/revision/latest?cb=20180328145536'},
      {img : 'https://banner2.kisspng.com/20171201/91b/batman-logo-png-5a20f3a8da66b7.6067604515121089688946.jpg'}
  
    ]
    return(
      <div style={{padding : '30px 0px 10px 0px'}}>
      <center><h3>Our Sponsors</h3></center>
      <InfiniteCarousel
        breakpoints={[
          {
            breakpoint: 500,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 2,
            },
          },
          {
            breakpoint: 768,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
            },
          },
        ]}
        dots={false}
        showSides={true}
        sidesOpacity={1}
        sideSize={.1}
        slidesToScroll={2}
        slidesToShow={7}
        scrollOnDevice={true}
        lazyLoad={true}
        swipe={true}
        slidesSpacing={10}
        responsive={true}
    >
      {sponsorValues.map(x => 
      <div style={{maxHeight: '100px'}} className="sponsorLogo">
        <img
          alt=''
          height="100"
          src={x.img}
        />
      </div>)}
    </InfiniteCarousel>
    </div>
    )
  }
}
