import React, { Component } from 'react';
import Layout from '../components/Layout';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Table from 'react-bootstrap/Table'
// import { Button, NavLink } from 'reactstrap';
import Speech from 'speak-tts'
// import Avatar from 'react-avatar';
import 'react-super-responsive-table/dist/SuperResponsiveTableStyle.css'
import '../css/col.css';
import '../css/about.css';

const speech = new Speech();

const bread = [
  {order : 2, title: "About", link : "/about"},
  {order : 1, title: "Home", link : "/"}
].sort((a,b) => a.order - b.order);

class index extends Component {
  constructor(props){
    super(props);
    this.state={
      loading : true,
      err : false
    }
  }

  componentWillMount(){

  }

  componentDidMount(){

  }
  componentDidCatch(error, errorInfo){
    this.setState({err : true})
  }

  
  render() {
    const sponsors = [
        // {name:"KFC", link : "https://www.google.com"},
        // {name : "Bahamas Air",link : "https://www.google.com"},
        // {name:"Costco", link : "https://www.google.com"},
        {name : "Carlisle Mott Financial",link : "http://www.carlislemott.com"}
        // {name : "Texas Instruments",link : "https://www.google.com"}
      ];
    const events = [
      {
        name : "A taste of Paradise Gala", 
        date: new Date("09/21/2019"), 
        time : "8:00 pm - 10:00 pm",
        location: "African American Museum of Dallas",
        address: "T3536 Grand Ave, Dallas, TX 75210BD", 
        mapURL : "https://goo.gl/maps/1K96gzDvLR2TiDn68", 
        detailsLink: "",
        calendarLink: "https://www.google.com/calendar/render?action=TEMPLATE&text=A+taste+of+Paradise+Gala&details=African+American+Museum+of+Dallas&location=T3536+Grand+Ave%2C+Dallas%2C+TX+75210BD&dates=20190922T000000Z%2F20190922T020000Z"
      }
      // {
      //   name : "Mommy and Tea", 
      //   date: new Date("09/21/2019"), 
      //   time : "10:00am - 2:00pm", 
      //   address: "TBD", 
      //   mapURL : "", 
      //   detailsLink: "",
      //   calendarLink: "http://www.google.com/calendar/event?action=TEMPLATE&dates=20190921T220000Z%2F20190921T230000Z&text=Ion%20Fashion%20Show&location=Outside&details=fpierjnpgifjernpigjnepr"

      // }
      // {
      //   name : "Mommy and Tea", 
      //   date: new Date("12/22/1988"), 
      //   time : "10:00am - 2:00pm", 
      //   address: "", 
      //   mapURL : "", 
      //   detailsLink: ""
      // },
      // {
      //   name : "Mommy and Tea", 
      //   date: new Date(), 
      //   time : "10:00am - 2:00pm", 
      //   address: "", 
      //   mapURL : "", 
      //   detailsLink: ""
      // }
    ].sort((a,b) => a.date - b.date);
    const values = [
      {
        title : "Our Mission",
        description : (<div><p>ION Bahamas is dedicated to uniting all Bahamians in the Dallas/Fort Worth Metroplex, as well as
          descendants of The Bahamas to continue expressing our standards of excellence,
          through our culture while educating, sharing and helping each other reach a
          common goal.</p></div>)
      },
      {
        title : "Our Vision",
        description : (<div><p>ION Bahamas will become the uniting force for all Bahamians in the Dallas/Fort Worth Metroplex, with an interest in connecting with other Bahamian Associations throughout the United States and Canada.</p></div>)
      },
      {
        title : "The Purpose",
        description : (<div><p>To educate, motivate, inspire and empower each other to a higher level of excellence in achieving our collective and individual goals.  This is done through
          workshops, seminars, networking events and other social events etc.  Guest speakers are invited to address concerns and issues important to us such as:</p>
          <ol type="I" className="column">
            <li>Immigration</li>
            <li>Taxes</li>
            <li>Financial Consultation</li>
            <li>Credit - Credit repair</li>
            <li>College life as an international student</li>
            <li>Career Planning</li>
