import React, { Component } from 'react';
import Layout from '../components/Layout';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import { Button } from 'reactstrap';
import { Carousel } from 'react-responsive-carousel';
import InfiniteCarousel from '../components/InfiniteCarousel';
import Membership from '../components/MembershipBoxes';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import '../css/carousel.css'
import '../css/col.css';
import '../css/meeting.css';

const bread = [
  {order : 1, title: "Home", link : "/"},
].sort((a,b) => a.order - b.order);

class home extends Component {
  constructor(props){
    super(props);
    this.state={
      loading : true,
      err : false
    }
  }

  componentWillMount(){

  }

  componentDidMount(){
    document.title = "Home";
  }
  componentDidCatch(error, errorInfo){
    this.setState({err : true})
  }

  
  render() {
    const hi = "This text is the content of the box. We have added a 50px padding, 20px margin and a 15px green border. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.";
    return (
      <div>
        <div className="highlights">
                  <Carousel
                    showArrows={true}
                    showThumbs={false}
                    infiniteLoop={true}
                    autoPlay={true}
                    useKeyboardArrows={true}
                    stopOnHover={true}
                    interval={10000}
                    transitionTime={350}
                    swipeable={true}
                    dynamicHeight={false}
                  >
                  {[1,2,3].map((x,i) => 
                      <div key={i}>
                          <img alt="" src="http://www.iccbc.com/wp-content/uploads/2017/05/asta.jpg" />
                          <div className="auctionTextContainer">
                              <p className="auctionTextDescription">
                                <ul>
                                  <li>EAT</li>
                                  <li>DRINK</li>
                                  <li>BID</li>
                                  <li>ENJOY</li>
                                </ul>
                                <h1 className="textTitle">ION Gala and Silent Auction</h1>
                                <center>123 Main St, Dallas TX, 12345</center>
                                <div className="dateContainer">
                                  <div className="fixed"><span>21</span></div>
                                  <div className="flex-item">
                                    <span className="month">September</span>
                                    <br /><span className="year">2019</span>
                                  </div>
                                </div>
                                <div >
                                  Performances by:
                                  <ul>
                                  <li>Singer 1</li>
                                  <li>Singer 2</li>
                                  </ul>
                                </div>
                                <Button href="/about"color="primary">Purchase Tickets</Button>
                              </p>
                              
                          </div>
                      </div>
                    )}
                    <div key={"Fashion Show"}>
                          <img alt="" src="https://www.britishfashioncouncil.com/uploads/files/1/News/david.jpg" />
                          <div className="carouselTextContainer">
                            <h1 className="textTitle">ION Fashion Show</h1>
                              <p className="textDescription">
                                <b>Come and join us "On the Catwalk"!!</b>
                                <center>123 Main St, Dallas TX, 12345</center>
                                <div className="dateContainer">
                                  <div className="fixed"><span>21</span></div>
                                  <div className="flex-item">
                                    <span className="month">September</span>
                                    <br /><span className="year">2019</span>
                                  </div>
                                </div>
                                <div >
                                  Performances by:
                                  <ul>
                                  <li>Singer 1</li>
                                  <li>Singer 2</li>
                                  </ul>
                                </div>
                              </p>
                              <Button href="/about"color="primary">Purchase Tickets</Button>
                          </div>
                      </div>
                  </Carousel>
                  {/* <InfiniteCarousel /> */}
                </div>
          <Container fluid={true} style={{paddingTop : '10px'}}>
            <Row noGutters={true} >
              <Col xs={12} md={{ span: 3, offset: 0 }}>
                <div className="meetingBox">
                  <div className="meetingBoxTitle">Shortcut Links</div>
                  <div  className="meetingBoxDescription">
                    <p>Quarterly meetings are held and open to the public. (Please check calendar for dates and times). Meetings usually open with a meet and greet, followed by a 45 minutes business session, with networking opportunities afterwards.</p>
                    <p>General meetings usually include a guest speaker to provide expertise on various topics that are pertinent to our community, such as:  Motivation, Wealth Building, Immigration, Taxes, Community Service and Outreach, Bahamian events, etc.</p>
                  </div>
                </div>
              </Col>
              <Col xs={12} md={{ span: 6, offset: 0 }}>
                <Membership />
              </Col>
              <Col xs={12} md={{ span:3, offset: 0 }}>
                <div className="meetingBox">
                  <div className="meetingBoxTitle">Quarterly Meetings</div>
                  <div  className="meetingBoxDescription">
                    <p>Quarterly meetings are held and open to the public. (Please check calendar for dates and times). Meetings usually open with a meet and greet, followed by a 45 minutes business session, with networking opportunities afterwards.</p>
                    <p>General meetings usually include a guest speaker to provide expertise on various topics that are pertinent to our community, such as:  Motivation, Wealth Building, Immigration, Taxes, Community Service and Outreach, Bahamian events, etc.</p>
                  </div>
                </div>
              </Col>
              {/* <Col xs={12} md={12}><div className="box">{hi}</div></Col>
              <Col xs={12} md={12}><div className="box">{hi}</div></Col>
              <Col xs={12} md={12}><div className="box">{hi}</div></Col>
              <Col xs={12} md={12}><div className="box">{hi}</div></Col>
              <Col xs={12} md={12}><div className="box">{hi}</div></Col>
              <Col xs={12} md={12}><div className="box">{hi}</div></Col> */}
            </Row>
          </Container>
        </div>
    );
  }
}

export default Layout(home);
