import React, { Component } from 'react';
import Layout from '../components/Layout';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import { Button } from 'react-bootstrap';
import { Carousel } from 'react-responsive-carousel';
import Membership from '../components/MembershipBoxes'
import "react-responsive-carousel/lib/styles/carousel.min.css";
import '../css/carousel.css'
import '../css/col.css';
import '../css/meeting.css';

const bread = [
  {order : 1, title: "Home", link : "/"},
].sort((a,b) => a.order - b.order);

class home extends Component {
  constructor(props){
    super(props);
    this.state={
      loading : true,
      err : false
    }
  }

  componentWillMount(){

  }

  componentDidMount(){
    document.title = "Home";
  }
  componentDidCatch(error, errorInfo){
    this.setState({err : true})
  }

  
  render() {
    const hi = "This text is the content of the box. We have added a 50px padding, 20px margin and a 15px green border. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.";
    return (
      <div>
        <div className="highlights">
                  <Carousel
                    showArrows={true}
                    showThumbs={false}
                    infiniteLoop={true}
                    autoPlay={false}
                    useKeyboardArrows={true}
                    stopOnHover={true}
                    interval={5000}
                    transitionTime={350}
                    swipeable={true}
                    dynamicHeight={false}
                  >
                  {[1,2,3,4].map((x,i) => 
                      <div key={i}>
                        {/* <div className="caroselLeftBox">
                          <p>nfoewno fnroegfd noirengoi jnfirewhfnoiegoir giroejgnoer</p>
                        </div> */}
                        {/* <div className="caroselRightBox"> */}
                          <img alt="" src="https://assets-edge.slickpic.com/img/homepage/slickpic-photo-hosting-and-sharing-quality.jpg" />
                          <div className="carouselTextContainer">
                            <h1 className="textTitle">Text Here</h1>
                            <p className="textDescription">Massa tincidunt dui ut ornare. Nunc congue nisi vitae suscipit tellus. Elit eget gravida cum sociis natoque penatibus et magnis. Fermentum et sollicitudin ac orci phasellus. Rutrum quisque non tellus orci. Facilisis sed odio morbi quis commodo. Sagittis eu volutpat odio facilisis mauris sit.</p>
                            <Button href="/about" variant="primary" size="lg" disabled>
                              Primary button
                            </Button>
                          </div>
                          {/* <p className="legend">Legend 2</p> */}
                        {/* </div> */}
                      </div>)}
                  </Carousel>
                </div>
          <Container fluid={true} style={{paddingTop : '10px'}}>
            <Row noGutters={true} >
              <Col xs={12} md={{ span: 3, offset: 0 }}>
                <div className="meetingBox">
                  <div className="meetingBoxTitle">Shortcut Links</div>
                  <div  className="meetingBoxDescription">
                    <p>Quarterly meetings are held and open to the public. (Please check calendar for dates and times). Meetings usually open with a meet and greet, followed by a 45 minutes business session, with networking opportunities afterwards.</p>
                    <p>General meetings usually include a guest speaker to provide expertise on various topics that are pertinent to our community, such as:  Motivation, Wealth Building, Immigration, Taxes, Community Service and Outreach, Bahamian events, etc.</p>
                  </div>
                </div>
              </Col>
              <Col xs={12} md={{ span: 6, offset: 0 }}>
                <Membership />
              </Col>
              <Col xs={12} md={{ span:3, offset: 0 }}>
                <div className="meetingBox">
                  <div className="meetingBoxTitle">Quarterly Meetings</div>
                  <div  className="meetingBoxDescription">
                    <p>Quarterly meetings are held and open to the public. (Please check calendar for dates and times). Meetings usually open with a meet and greet, followed by a 45 minutes business session, with networking opportunities afterwards.</p>
                    <p>General meetings usually include a guest speaker to provide expertise on various topics that are pertinent to our community, such as:  Motivation, Wealth Building, Immigration, Taxes, Community Service and Outreach, Bahamian events, etc.</p>
                  </div>
                </div>
              </Col>
              {/* <Col xs={12} md={12}><div className="box">{hi}</div></Col>
              <Col xs={12} md={12}><div className="box">{hi}</div></Col>
              <Col xs={12} md={12}><div className="box">{hi}</div></Col>
              <Col xs={12} md={12}><div className="box">{hi}</div></Col>
              <Col xs={12} md={12}><div className="box">{hi}</div></Col>
              <Col xs={12} md={12}><div className="box">{hi}</div></Col> */}
            </Row>
          </Container>
        </div>
    );
  }
}

export default Layout(home);
