import React, { Component } from 'react';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Layout from '../components/Layout';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import Divider from '@material-ui/core/Divider';
import ListItemText from '@material-ui/core/ListItemText';
import Typography from '@material-ui/core/Typography';
import {
  BigCalendar,
  DateLocalizer,
  momentLocalizer,
  globalizeLocalizer,
  move,
  Views,
  Navigate,
  components,
} from 'react-big-calendar'
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

const bread = [
  {order : 2, title: "Calendar", link : "/calendar"},
  {order : 1, title: "Home", link : "/"}
].sort((a,b) => a.order - b.order);

const localizer = momentLocalizer(moment)

const events = [
  {
    allDay: false,
    startDate: new Date("09/21/2019"),
    endDate: new Date("09/21/2019"),
    title: 'A taste of Paradise Gala',
    description : 'ifonfoirenoigferognoer'
  },
  {
    allDay: true,
    startDate: new Date('December 09, 2017 11:13:00'),
    endDate: new Date('December 09, 2017 11:13:00'),
    title: 'All Day Event',
  },
  {
    allDay: true,
    startDate: new Date("06/15/2019"),
    endDate: new Date(),
    title: 'All Day Event',
  }
  ].filter(x => x.startDate > new Date(Date.now() - (1000 * 60 * 60 * 24 * 30)));

  const allViews = Object.keys(BigCalendar.Views).map(k => BigCalendar.Views[k]);

  class donate extends Component {
    constructor(props){
      super(props);
      this.state={
        loading : true,
        err : false,
        selectedDate : new Date(),
        date: new Date(2015, 3, 12),
        width: 500
      }
    }

    componentWillMount(){

    }

    componentDidMount(){
      window.addEventListener("resize", () => {
        this.setState({
          width: window.innerWidth,
          height: window.innerHeight
        });
      });

    }
    componentDidCatch(error, errorInfo){
      this.setState({err : true})
    }

    render() {
      return (
        <Container fluid={true}>
          <Row>
            <Col xs={{size :6, order : 2 }} md={{size :6 , order : 1}}>
              <div style={{padding : '20px 20px 0px 20px'}}>
                <BigCalendar
                  style={{ height: 500, width: this.state.width }}
                  // style={{ minHeight : '600px'}}
                  localizer={localizer}
                  events={events}
                  culture='en-GB'
                  views={['month'/*, 'week'*/]}
                  startAccessor="startDate"
                  endAccessor="endDate"
                  onSelectEvent={event => console.log(event)}
                  onNavigate={(date) => { 
                    console.log(date)
                    // this.setState({ selectedDate: date })
                  }}
                  
                  toolbar={false}
                  step={60}
                  views={allViews}
                  view={this.state.view}
                  onView={() => {}}
                  date={this.state.date}
                  onNavigate={date => this.setState({ date })}
                />
              </div>
            </Col>
            <Col xs={{size :6 , order: 1 }} md={{size: 6 , order : 2}}>
              <div style={{padding : '0px 20px 0px 0px'}}>
                <center>
                  <h2>UpComing Events</h2>
                </center>
                <List>
                  {events
                  .filter(x => x.endDate >= new Date().setHours(0,0,0,0))
                  .map(x => {
                    return <div>
                      <ListItem alignItems="flex-start" onClick={(event) => console.log("Hello")}>
                        <ListItemText 
                          primary={x.title}
                          secondary={
                            <React.Fragment>
                              <Typography
                                component="span"
                                variant="body2"
                                color="textPrimary"
                              >
                                {x.startDate.toString()}
                              </Typography>
                              {' - '+x.description}
                            </React.Fragment>
                            }
                        />
                      </ListItem>
                      </div>
                    })}
                </List>
              </div>
            </Col>
          </Row>
          </Container>
      );
    }
}

export default Layout(donate,bread);
