import React, { Component } from 'react';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Layout from '../components/Layout';
import {
  Calendar,
  DateLocalizer,
  momentLocalizer,
  globalizeLocalizer,
  move,
  Views,
  Navigate,
  components,
} from 'react-big-calendar'
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

const bread = [
  {order : 2, title: "Donate", link : "/donate"},
  {order : 1, title: "Home", link : "/"}
].sort((a,b) => a.order - b.order);

const localizer = momentLocalizer(moment)

const events = [
  {
    allDay: false,
    endDate: new Date("09/21/2019"),
    startDate: new Date("09/21/2019"),
    title: 'A taste of Paradise Gala',
  },
  {
    allDay: true,
    endDate: new Date('December 09, 2017 11:13:00'),
    startDate: new Date('December 09, 2017 11:13:00'),
    title: 'All Day Event',
  },
  ];

class donate extends Component {
  constructor(props){
    super(props);
    this.state={
      loading : true,
      err : false
    }
  }

  componentWillMount(){

  }

  componentDidMount(){

  }
  componentDidCatch(error, errorInfo){
    this.setState({err : true})
  }

  
  render() {
    return (
      <Container fluid={true}>
        <Row>
          <Col xs={12} md={12}>
          <div style={{padding : '20px 20px 0px 20px'}}>
            <Calendar
              style={{minHeight : '600px'}}
              localizer={localizer}
              events={events}
              culture='en-GB'
              views={['month', 'week']}
              startAccessor="startDate"
              endAccessor="endDate"
              onSelectEvent={event => console.log(event)}
            />
          </div>
          </Col>
         </Row>
        <Row>
          <Col xs={12} md={12}>
          <div style={{padding : '20px 20px 0px 20px'}}>
            <Calendar
              style={{minHeight : '600px'}}
              localizer={localizer}
              events={events}
              culture='en-GB'
              views={['month', 'week']}
              startAccessor="startDate"
              endAccessor="endDate"
              onSelectEvent={event => console.log(event)}
            />
          </div>
          </Col>
         </Row>
        </Container>
      
    );
  }
}

export default Layout(donate,bread);
