import React, { Component } from 'react';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Layout from '../components/Layout';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import Divider from '@material-ui/core/Divider';
import ListItemText from '@material-ui/core/ListItemText';
import Typography from '@material-ui/core/Typography';
import {
  Calendar,
  DateLocalizer,
  momentLocalizer,
  globalizeLocalizer,
  move,
  Views,
  Navigate,
  components,
} from 'react-big-calendar'
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

const bread = [
  {order : 2, title: "Calendar", link : "/calendar"},
  {order : 1, title: "Home", link : "/"}
].sort((a,b) => a.order - b.order);

const localizer = momentLocalizer(moment)

const events = [
  {
    allDay: false,
    endDate: new Date("09/21/2019"),
    startDate: new Date("09/21/2019"),
    title: 'A taste of Paradise Gala',
  },
  {
    allDay: false,
    endDate: new Date(),
    startDate: new Date(),
    title: 'A taste of Paradise Gala',
  },
  {
    allDay: true,
    endDate: new Date('December 09, 2017 11:13:00'),
    startDate: new Date('December 09, 2017 11:13:00'),
    title: 'All Day Event',
  },
  ].filter(x => x.endDate >= new Date().setHours(0,0,0,0));
  
class donate extends Component {
  constructor(props){
    super(props);
    this.state={
      loading : true,
      err : false
    }
  }

  componentWillMount(){

  }

  componentDidMount(){

  }
  componentDidCatch(error, errorInfo){
    this.setState({err : true})
  }

  
  render() {
    return (
      <Container fluid={true}>
        <Row>
          <Col xs={{size :6, order : 2 }} md={{size :6 , order : 1}}>
            <div style={{padding : '20px 20px 0px 20px'}}>
              <Calendar
                style={{minHeight : '600px'}}
                localizer={localizer}
                events={events}
                culture='en-GB'
                views={['month'/*, 'week'*/]}
                startAccessor="startDate"
                endAccessor="endDate"
                onSelectEvent={event => console.log(event)}
              />
            </div>
          </Col>
          <Col xs={{size :6 , order: 1 }} md={{size: 6 , order : 2}}>
            <div style={{padding : '0px 20px 20px 0px'}}>
              <center>
                <h2>UpComing Events</h2>
              </center>
              <List>
                {events.map(x => {
                return <div>
                  <ListItem alignItems="flex-start">
                    <ListItemText 
                      primary={x.title}
                      secondary={
                        <React.Fragment>
                          <Typography
                            component="span"
                            variant="body2"
                            color="textPrimary"
                          >
                            Ali Connors
                          </Typography>
                          {" — I'll be in your neighborhood doing errands this…"}
                          </React.Fragment>
                        }
                    />
                  </ListItem>
                  <Divider variant="inset" component="li" />
                  </div>
                  })}
              </List>
            </div>
          </Col>
         </Row>
        </Container>
      
    );
  }
}

export default Layout(donate,bread);
